import { Formik, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import InputField from "../components/form-elements/InputField";
import Button from "../components/buttons/FormSubmitButton";
import FormTitle from "../components/titles/FormTitle";
import FormWrapper from "./FormWrapper";

export default function GeneratePage() {
  // Yup doğrulama şeması
  const validationSchema = Yup.object({
    prompt: Yup.string()
      .required("Prompt is required")
      .min(5, "Prompt must be at least 5 characters"),
    negative_prompt: Yup.string().required("Negative prompt is required"),
  });

  // Başlangıç değerleri
  const initialValues = {
    prompt: "",
    negative_prompt: "",
  };

  // Form gönderimi
  const handleSubmit = (values, { resetForm }) => {
    console.log("Prompt:", values.prompt);
    console.log("Negative Prompt:", values.negative_prompt);
    resetForm();
  };

  return (
    <Formik
      initialValues={initialValues}
      validationSchema={validationSchema}
      onSubmit={handleSubmit}
    >
      {({ isSubmitting }) => (
        <FormWrapper>
          <FormTitle text="🖼️ Text to Image Form 🖼️" />

          {/* Prompt alanı */}
          <Field
            name="prompt"
            component={InputField}
            label="✨ Prompt"
            placeholder="What's on your mind?"
          />
          <ErrorMessage
            name="prompt"
            component="div"
            className="text-red-500 text-sm mt-1"
          />

          {/* Negative prompt alanı */}
          <Field
            name="negative_prompt"
            component={InputField}
            label="🚫 Negative Prompt"
            placeholder="Anything to avoid?"
          />
          <ErrorMessage
            name="negative_prompt"
            component="div"
            className="text-red-500 text-sm mt-1"
          />

          {/* Gönder butonu */}
          <div className="flex items-center justify-center">
            <Button
              type="submit"
              text="🚀 Generate Image"
              disabled={isSubmitting}
            />
          </div>
        </FormWrapper>
      )}
    </Formik>
  );
}
